export const getScorecardColorVariable = (
  levelIndex: number,
  scorecardLevelsLength: number,
): string => {
  let colorLevels = scorecardLevelsLength;

  if (colorLevels > 5) {
    colorLevels = 5;
  }

  if (colorLevels < 2) {
    colorLevels = 2;
  }

  return `--scorecard-level-color-${colorLevels}-${
    levelIndex + 1 > colorLevels ? colorLevels : levelIndex + 1
  }`;
};
